import { AfterViewInit, Component, ElementRef, OnInit, ViewChild } from '@angular/core';

@Component({
  selector: 'app-education',
  templateUrl: './education.component.html',
  styleUrls: ['./education.component.scss']
})
export class EducationComponent implements AfterViewInit {
  @ViewChild('firstComponent', { static: false }) firstComponent!: ElementRef;
  ngAfterViewInit(): void {
    const firstComponentHeight = this.firstComponent.nativeElement.offsetHeight;
    const secondComponentElement = document.getElementById('secondComponent');
    if (secondComponentElement) {
      secondComponentElement.style.marginTop = firstComponentHeight + 'px';
    }
  }



}

